import database from '@/components/firebaseInit.js';
import VTabsSlider from "vuetify/lib/components/VTabs/VTabsSlider";
export default {
  components: {
    VTabsSlider: VTabsSlider
  },
  data: function data() {
    return {
      valid: true,
      active: null,
      img: '',
      title: '',
      titleRules: [function (v) {
        return !!v || 'Title is Required';
      }],
      summary: '',
      summaryRules: [function (v) {
        return !!v || 'Summary is Required';
      }],
      ingredients: '',
      ingredientsRules: [function (v) {
        return !!v || 'Ingredients are Required';
      }],
      directions: '',
      directionsRules: [function (v) {
        return !!v || 'Directions are Required';
      }],
      tabNames: ['summary', 'ingredients', 'directions']
    };
  },
  methods: {
    getIMG: function getIMG(val) {
      if (val !== "") {
        this.img = "https://source.unsplash.com/1600x900/?" + val;
      }
    },
    submit: function submit() {
      if (this.$refs.form.validate()) {
        database.collection('recipes').add({
          title: this.title,
          summary: this.summary,
          ingredients: this.ingredients,
          directions: this.directions
        }).then(function (docRef) {
          console.log('Document written with ID: ', docRef.id);
        }).catch(function (error) {
          console.error('Error adding document: ', error);
          alert('An error has occurred while attempting to save to the database.');
        });
        this.clear();
      }
    },
    clear: function clear() {
      this.$refs.form.reset();
    }
  }
};
